// Get the enhanced resume content from the AI agent
let enhancedContent = $input.first().json.output;

// Clean up any unwanted HTML artifacts or markdown formatting
enhancedContent = enhancedContent
    .replace(/^```html\s*/i, '')  // Remove opening ```html
    .replace(/\s*```\s*$/i, '')   // Remove closing ```
    .replace(/^`html\s*/i, '')    // Remove `html
    .replace(/^html\s*/i, '')     // Remove html at start
    .replace(/^\s*}\s*html\s*/i, '') // Remove }html
    .replace(/^\s*`\s*/i, '')     // Remove leading backticks
    .trim();

// HTML template with styling to match original resume format exactly
const htmlTemplate = `
<!DOCTYPE html>
<html>
<head>
    <meta charset="UTF-8">
    <style>
        body {
            font-family: Arial, sans-serif;
            max-width: 800px;
            margin: 0 auto;
            padding: 40px;
            line-height: 1.3;
            color: #333;
            background-color: white;
            font-size: 14px;
        }

        h1 {
            font-size: 28px;
            font-weight: bold;
            margin-bottom: 8px;
            margin-top: 0;
            color: #000;
        }

        .job-title {
            font-size: 16px;
            font-weight: bold;
            margin-bottom: 15px;
            margin-top: 5px;
            color: #333;
        }

        .contact-section {
            margin-bottom: 20px;
        }

        .contact-section strong {
            font-weight: bold;
            display: block;
            margin-bottom: 8px;
        }

        .contact-section ul {
            list-style: disc;
            margin-left: 18px;
            margin-top: 0;
            margin-bottom: 0;
            padding: 0;
        }

        .contact-section li {
            margin-bottom: 2px;
            line-height: 1.2;
        }

        h2 {
            font-size: 16px;
            font-weight: bold;
            margin-top: 20px;
            margin-bottom: 10px;
            color: #000;
        }

        .job-entry {
            margin-bottom: 20px;
        }

        .job-title-line {
            font-weight: bold;
            margin-bottom: 2px;
            line-height: 1.2;
        }

        .job-company {
            font-style: italic;
            margin-bottom: 8px;
            line-height: 1.2;
        }

        .job-duties ul {
            list-style: disc;
            margin-left: 18px;
            margin-top: 5px;
            margin-bottom: 0;
            padding: 0;
        }

        .job-duties li {
            margin-bottom: 5px;
            line-height: 1.3;
        }

        p {
            margin-bottom: 10px;
            margin-top: 0;
            text-align: justify;
            line-height: 1.3;
        }

        a {
            color: #0066cc;
            text-decoration: none;
        }

        a:hover {
            text-decoration: underline;
        }

        .summary {
            text-align: justify;
            margin-bottom: 20px;
            line-height: 1.3;
        }

        /* Ensure proper spacing for different elements */
        strong {
            font-weight: bold;
        }

        em {
            font-style: italic;
        }

        /* Remove extra spacing from nested elements */
        ul {
            margin-top: 0;
            margin-bottom: 0;
        }

        li {
            line-height: 1.3;
        }
    </style>
</head>
<body>
    ${enhancedContent}
</body>
</html>
`;

// Return the formatted HTML
return [{ 
    json: { 
        enhanced_resume_html: htmlTemplate,
        enhanced_resume_formatted: enhancedContent,
        original_ai_output: enhancedContent
    } 
}];
